// --- 1. RECIBIR DATOS DEL NODO ANTERIOR ---
const datosCrudos = $input.first().json.datosCrudos;
const allChunks = [];

if (!Array.isArray(datosCrudos) || datosCrudos.length === 0) {
  throw new Error("Error: El JSON pegado está vacío o no es un array.");
}

// --- 2. FUNCIÓN "CHUNKER" REUTILIZABLE ---
function processPoi(poi) {
  const chunks = [];

  const baseMetadata = {
    nombre_atraccion: poi.nombre_atraccion || "Sin Nombre",
    categoria_primaria: poi.categoria || poi.categoria_primaria || "General",
    ubicacion_general: poi.ubicacion || poi.ubicacion_general || "Chihuahua",
    fuente_url: poi.fuente_url || "Sin Fuente"
  };

  // Chunk de Guía
  if (poi.descripcion_guia && poi.descripcion_guia.trim().length > 10) {
    chunks.push({
      pageContent: poi.descripcion_guia.trim(),
      metadata: { ...baseMetadata, tipo_chunk: "Guia" }
    });
  }

  // Chunk de Opinión
  if (poi.opinion_destacada && poi.opinion_destacada.trim().length > 10) {
    chunks.push({
      pageContent: poi.opinion_destacada.trim(),
      metadata: { ...baseMetadata, tipo_chunk: "Opinion" }
    });
  }

  // Chunk de Datos
  if (poi.datos_utiles && poi.datos_utiles.trim().length > 10) {
    chunks.push({
      pageContent: poi.datos_utiles.trim(),
      metadata: { ...baseMetadata, tipo_chunk: "Datos" }
    });
  }

  return chunks;
}

// --- 3. DETECCIÓN DEL TIPO DE ENTRADA ---
const primerElemento = datosCrudos[0];

if (primerElemento.analisis_categorias) {
  // CASO A: REPORTE DE ANÁLISIS
  const categorias = primerElemento.analisis_categorias || [];
  for (const categoria of categorias) {
    const segmentos = categoria.segmentos || [];
    for (const segmento of segmentos) {
      const pois = segmento.pois || [];
      for (const poi of pois) {
        allChunks.push(...processPoi(poi));
      }
    }
  }
} else if (primerElemento.nombre_atraccion) {
  // CASO B: ARRAY SIMPLE DE POIs
  for (const poi of datosCrudos) {
    allChunks.push(...processPoi(poi));
  }
} else {
  throw new Error("Error: El formato del JSON pegado no se reconoce.");
}

// --- 4. FORMATO FINAL PARA N8N + QDRANT ---
// Cada item debe ser { json: { pageContent, metadata }}
return allChunks.map(chunk => ({
  JSON: {
    pageContent: chunk.pageContent,
    metadata: chunk.metadata
  }
}));
